mapper.select:
SELECT * FROM wh_cfg_adv t WHERE t.cfgadvpagetype = '2016122100000001' and t.cfgadvstate = 1
and t.cfgavenueid = #{venueid}
order by t.cfgadvidx

SELECT * FROM wh_cfg_list t WHERE t.cfgpagetype = '2016101900000001' AND t.cfgenttype = '2016101400000052' AND t.cfgstate = 1
and t.cfgvenueid = #{venueid}
ORDER BY t.cfgshowidx

SELECT * FROM wh_cfg_list t WHERE t.cfgpagetype = '2016101900000001' AND t.cfgenttype = '2016112900000007' AND t.cfgstate = 1
and t.cfgvenueid = #{venueid}
ORDER BY t.cfgshowidx

SELECT t.traid, t.trashorttitle, t.tratitle, t.trapic FROM wh_train t WHERE t.trastate = 3
and t.travenueid = #{venueid}
ORDER BY t.trasdate DESC
