mapper.select:
	select * from wh_comment where 1=1 

	and rmrefid IN (select rmid from wh_comment 
	where rmreftyp=#{rmreftyp}
	) or  rmreftyp=#{rmreftyp}


      order by ${sort} ${order}

select * from wh_comment t1, wh_user t2 where t1.rmuid= t2.id and t1.rmtyp= 0 and t1.rmstate = 1
and t1.rmreftyp = #{reftyp}
and t1.rmrefid = #{refid}
order by t1.rmdate desc

SELECT u.name,u.phone,c.rmid,c.rmuid,c.rmdate,c.rmcontent,c.rmtitle,c.rmurl, c.rmreftyp,c.rmrefid,c.rmtyp,c.rmstate from wh_comment c, wh_user u where rmtyp = '0' and c.rmuid=u.id
and rmuid = #{rmuid}
and rmreftyp = #{rmreftyp}
order by rmdate desc

select t1.*,u.nickname, ( select count(*) from wh_comment t where t.rmtyp='1' and t.rmreftyp='2016102200000003' and t.rmrefid=t1.rmid) ishf from wh_comment t1,wh_user u where t1.rmtyp = '0' and u.id=t1.rmuid
and rmreftyp=#{rmreftyp}
and rmcontent like '%${rmcontent}%'
order by ${sort} ${order}
order by t1.rmdate desc

select t1.*,u.nickname, ( select count(*) from wh_comment t where t.rmtyp='1' and t.rmrefid=t1.rmid) ishf
from wh_comment t1,wh_user u
where t1.rmtyp = '0' and u.id=t1.rmuid and rmreftyp=#{rmreftyp}

	and rmcontent like '%${rmcontent}%'



		order by ${sort} ${order}


		order by t1.rmdate desc

SELECT c.*,IFNULL(m.account,'超级管理员') as account from wh_comment c LEFT JOIN whg_sys_user m on c.rmuid = m.id WHERE c.rmtyp=1
AND rmrefid=#{rmrefid}
order by c.rmdate

select c.rmid as itemId,c.rmuid as userId, u.headimgurl,c.rmcontent ,c.rmdate from wh_comment c,whg_usr_weixin u where c.rmuid=u.userid

	and and c.rmuid=#{userId}



	and c.rmrefid = #{itemId}



	and c.rmreftyp= #{type}

SELECT
	rmid,
	rmuid,
	rmdate,
	rmcontent,
	rmpltype,
	rmtitle,
	rmurl,
	rmreftyp,
	rmrefid,
	rmrefid,
	rmtyp,
	rmstate,
	rmvenueid
FROM wh_comment

	1 = 1

		AND rmid = #{whComment.rmid}


		AND rmuid = #{whComment.rmuid}


		AND rmdate = #{whComment.rmdate}


		AND rmcontent = #{whComment.rmcontent}


		AND rmpltype = #{whComment.rmpltype}


		AND rmtitle = #{whComment.rmtitle}


		AND rmurl = #{whComment.rmurl}


		AND rmreftyp = #{whComment.rmreftyp}


		AND rmrefid = #{whComment.rmrefid}


		AND rmtyp = #{whComment.rmtyp}


		AND rmstate = #{whComment.rmstate}


		AND rmvenueid = #{whComment.rmvenueid}


ORDER BY rmdate DESC
