mapper.select:
select
t.*,
(SELECT b.name FROM wh_branch b WHERE t.branch = b.id) AS branchName
from whg_specil_resource t
WHERE 1=1 and t.intype = 1

    AND t.name like "%"#{name}"%"


    AND t.state = #{state}


    AND t.state IN

        #{item}



    AND t.isdel = #{isdel}


    AND t.isdel = 0


    AND t.id IN

        #{item}




        ORDER BY t.${sort}
         ${order} 


        ORDER BY t.crtdate DESC

select t.* from whg_specil_resource t WHERE 1=1 and t.intype = 2
AND t.name like "%"#{name}"%"
AND t.state = #{state}

SELECT * FROM whg_specil_resource t LEFT JOIN wh_user u ON t.userid = u.id WHERE 1 = 1 AND t.intype = 2 AND t.isdel = 0
AND t.userid = #{userId}
ORDER BY t.statemdfdate DESC

SELECT waa.*, (SELECT COUNT(wc.cmid) FROM wh_collection wc WHERE wc.cmrefid = waa.id AND wc.cmopttyp = 2) AS dianzan, (SELECT COUNT(wc.cmid) FROM wh_collection wc WHERE wc.cmrefid = waa.id AND wc.cmopttyp = 0) AS shoucang FROM whg_specil_resource waa WHERE 1 = 1 AND waa.state = 6 AND waa.intype = 2 AND waa.isdel = 0
AND waa.type = #{type}
AND waa.areaid = #{areaId}
ORDER BY waa.statemdfdate DESC

SELECT waa.*, (SELECT COUNT(wc.cmid) FROM wh_collection wc WHERE wc.cmrefid = waa.id AND wc.cmopttyp = 2) AS dianzan, (SELECT COUNT(wc.cmid) FROM wh_collection wc WHERE wc.cmrefid = waa.id AND wc.cmopttyp = 0) AS shoucang FROM whg_specil_resource waa WHERE 1 = 1 AND waa.state = 6 AND waa.intype = 1 AND waa.isdel = 0
AND waa.type = #{type}
ORDER BY waa.crtdate DESC
